function liste_hymnes(periode){
  return Object.keys(hymnes[periode]);
}

function hymne_aelf2bref(titre){
    var titre_clean = titre.replaceAll("'", "’").trim();
    if (titre_clean.slice(-1) == "." || titre_clean.slice(-1) == ","){
        titre_clean = titre_clean.slice(0, -1);
    }
    return titre_clean
}

function hymne_from_titre(titre){
  let titre_clean = hymne_aelf2bref(titre);
  let texte = "";
    for (var periode of Object.keys(hymnes)){
        if (hymnes[periode].hasOwnProperty(titre_clean)){
            texte = hymnes[periode][titre_clean];
            break;
        }
    }
    return texte;
}

function hymne_in_periode(titre, periode){
    let titre_clean = hymne_aelf2bref(titre);
    if (hymnes[periode].hasOwnProperty(titre_clean)){
        return true;
    }
    return false;
}

